import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { fetchAWSStatus } from '../features/awsServices/awsServicesSlice';
import { 
    Grid, 
    Card, 
    CardContent, 
    Typography, 
    CircularProgress,
    Alert,
    Box,
    Paper,
    Container,
    Divider,
    useTheme,
    LinearProgress,
    TextField,
    Select,
    MenuItem,
    InputLabel,
    FormControl,
    Chip
} from '@mui/material';
import WebsiteMonitoring from './WebsiteMonitoring';
import LogManagement from './LogManagement';
import CloudIcon from '@mui/icons-material/Cloud';
import StorageIcon from '@mui/icons-material/Storage';
import FunctionsIcon from '@mui/icons-material/Functions';
import DnsIcon from '@mui/icons-material/Dns';
import HubIcon from '@mui/icons-material/Hub';
import AlertsManager from './AlertsManager';
import ResourceActions from './ResourceActions';
import { styled } from '@mui/material/styles';
import DashboardIcon from '@mui/icons-material/Dashboard';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';
import APMDashboard from './APMDashboard';
import RUMDashboard from './RUMDashboard';
import DatabaseMetrics from './DatabaseMetrics';

// Register ChartJS components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const StyledCard = styled(Card)(({ theme }) => ({
  height: '100%',
  display: 'flex',
  flexDirection: 'column',
  position: 'relative',
  '&:hover': {
    transform: 'translateY(-4px)',
    transition: 'transform 0.3s ease'
  }
}));

const MetricValue = styled(Typography)(({ theme }) => ({
  fontSize: '2rem',
  fontWeight: 'bold',
  color: theme.palette.primary.main
}));

const ServiceCard = ({ title, icon, data, children }) => {
    const theme = useTheme();

    return (
        <Card 
            elevation={2}
            sx={{
                height: '100%',
                transition: 'transform 0.2s, box-shadow 0.2s',
                '&:hover': {
                    transform: 'translateY(-4px)',
                    boxShadow: theme.shadows[4],
                }
            }}
        >
            <CardContent>
                <Box display="flex" alignItems="center" mb={2}>
                    {icon}
                    <Typography variant="h6" ml={1}>
                        {title}
                    </Typography>
                </Box>
                <Divider sx={{ mb: 2 }} />
                {children}
            </CardContent>
        </Card>
    );
};

const Dashboard = () => {
    const dispatch = useDispatch();
    const { data, status, error } = useSelector((state) => state.awsServices);
    const theme = useTheme();

    useEffect(() => {
        const fetchData = async () => {
            try {
                const [statusResponse, dbMetricsResponse] = await Promise.all([
                    fetch('http://localhost:5001/api/status'),
                    fetch('http://localhost:5001/api/db-metrics')
                ]);

                const statusData = await statusResponse.json();
                const dbMetricsData = await dbMetricsResponse.json();

                // Update your Redux store or state management
                dispatch(updateData({
                    ...statusData,
                    dbMetrics: dbMetricsData
                }));
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };

        fetchData();
        const interval = setInterval(fetchData, 30000);
        return () => clearInterval(interval);
    }, [dispatch]);

    useEffect(() => {
        console.log('Current data:', data);
        console.log('Current status:', status);
        console.log('Current error:', error);
    }, [data, status, error]);

    if (status === 'loading' && !data) {
        return (
            <Box display="flex" justifyContent="center" alignItems="center" minHeight="80vh">
                <CircularProgress />
            </Box>
        );
    }

    if (status === 'failed') {
        return (
            <Alert severity="error" sx={{ m: 2 }}>
                {error || 'Failed to fetch AWS services status'}
            </Alert>
        );
    }

    return (
        <Container maxWidth="xl" sx={{ py: 4 }}>
            <Typography variant="h4" gutterBottom sx={{ 
                color: theme.palette.primary.main,
                fontWeight: 'bold',
                mb: 4
            }}>
                AWS Infra
            </Typography>

            <Grid container spacing={3}>
                <Grid item xs={12}>
                    <AlertsManager 
                        metrics={{
                            cpu: data?.ec2?.map(instance => instance.cpuUtilization) || [],
                            memory: data?.ec2?.map(instance => instance.memoryUtilization) || []
                        }} 
                    />
                </Grid>

                <Grid item xs={12}>
                    <StyledCard>
                        <CardContent>
                            <Grid container spacing={3}>
                                <Grid item xs={12} md={3}>
                                    <Box display="flex" flexDirection="column" alignItems="center">
                                        <DashboardIcon sx={{ fontSize: 40, color: 'primary.main', mb: 1 }} />
                                        <Typography variant="h6" gutterBottom>Total Resources</Typography>
                                        <MetricValue>{data?.ec2?.length + data?.rds?.length || 0}</MetricValue>
                                    </Box>
                                </Grid>
                                <Grid item xs={12} md={3}>
                                    <Typography variant="h6">Running Instances</Typography>
                                    <Typography variant="h4">
                                        {data?.ec2?.filter(i => i.state === 'running').length || 0}
                                    </Typography>
                                </Grid>
                                <Grid item xs={12} md={3}>
                                    <Typography variant="h6">Average CPU</Typography>
                                    <Typography variant="h4">
                                        {data?.ec2?.reduce((acc, curr) => acc + (curr.cpuUtilization || 0), 0) / data?.ec2?.length || 0}%
                                    </Typography>
                                </Grid>
                                <Grid item xs={12} md={3}>
                                    <Typography variant="h6">Alerts</Typography>
                                    <Typography variant="h4" color="error">
                                        {data?.ec2?.filter(i => i.cpuUtilization > 80).length || 0}
                                    </Typography>
                                </Grid>
                            </Grid>

                            <Box mt={4} height={200}>
                                <Line
                                    data={{
                                        labels: data?.ec2?.map(i => i.id) || [],
                                        datasets: [{
                                            label: 'CPU Utilization',
                                            data: data?.ec2?.map(i => i.cpuUtilization) || [],
                                            borderColor: theme.palette.primary.main,
                                            tension: 0.4
                                        }]
                                    }}
                                    options={{
                                        responsive: true,
                                        maintainAspectRatio: false,
                                        plugins: {
                                            legend: {
                                                position: 'top',
                                            }
                                        }
                                    }}
                                />
                            </Box>
                        </CardContent>
                    </StyledCard>
                </Grid>

                <Grid item xs={12}>
                    <Paper sx={{ p: 2, mb: 3 }}>
                        <Grid container spacing={2} alignItems="center">
                            <Grid item xs={12} md={4}>
                                <TextField
                                    fullWidth
                                    label="Search Resources"
                                    variant="outlined"
                                    size="small"
                                />
                            </Grid>
                            <Grid item xs={12} md={3}>
                                <FormControl fullWidth size="small">
                                    <InputLabel>Status</InputLabel>
                                    <Select>
                                        <MenuItem value="all">All</MenuItem>
                                        <MenuItem value="running">Running</MenuItem>
                                        <MenuItem value="stopped">Stopped</MenuItem>
                                    </Select>
                                </FormControl>
                            </Grid>
                            <Grid item xs={12} md={3}>
                                <FormControl fullWidth size="small">
                                    <InputLabel>Resource Type</InputLabel>
                                    <Select>
                                        <MenuItem value="all">All</MenuItem>
                                        <MenuItem value="ec2">EC2</MenuItem>
                                        <MenuItem value="rds">RDS</MenuItem>
                                    </Select>
                                </FormControl>
                            </Grid>
                        </Grid>
                    </Paper>
                </Grid>

                {/* EC2 Instances */}
                <Grid item xs={12} md={6}>
                    <ServiceCard 
                        title="EC2 Instances" 
                        icon={<CloudIcon sx={{ color: theme.palette.primary.main }} />}
                    >
                        {data?.ec2?.map((instance) => (
                            <Paper 
                                key={instance.id} 
                                sx={{ 
                                    p: 2, 
                                    mb: 2,
                                    backgroundColor: theme.palette.background.default,
                                    '&:hover': {
                                        boxShadow: 3,
                                        transition: 'box-shadow 0.3s ease-in-out'
                                    }
                                }}
                            >
                                <Grid container spacing={2}>
                                    <Grid item xs={12} md={8}>
                                        <Typography variant="subtitle1" fontWeight="bold">
                                            ID: {instance.id}
                                        </Typography>
                                        <Typography>
                                            State: <Chip 
                                                label={instance.state}
                                                color={instance.state === 'running' ? 'success' : 'error'}
                                                size="small"
                                            />
                                        </Typography>
                                        <Typography>Type: {instance.type}</Typography>
                                        <Typography>
                                            Launch Time: {
                                                instance.launch_time ? 
                                                new Date(instance.launch_time).toLocaleString() : 
                                                'N/A'
                                            }
                                        </Typography>
                                    </Grid>
                                    <Grid item xs={12} md={4}>
                                        <Box display="flex" flexDirection="column" alignItems="center">
                                            <CircularProgress 
                                                variant="determinate" 
                                                value={instance.cpuUtilization || 0}
                                                size={80}
                                                sx={{
                                                    color: instance.cpuUtilization > 80 ? 'error.main' : 
                                                           instance.cpuUtilization > 60 ? 'warning.main' : 
                                                           'success.main'
                                                }}
                                            />
                                            <Typography variant="body2" color="textSecondary" mt={1}>
                                                CPU: {instance.cpuUtilization}%
                                            </Typography>
                                        </Box>
                                    </Grid>
                                </Grid>
                                <Box mt={2}>
                                    <ResourceActions resourceId={instance.id} resourceType="ec2" />
                                </Box>
                            </Paper>
                        ))}
                    </ServiceCard>
                </Grid>

                {/* RDS Instances */}
                <Grid item xs={12} md={6}>
                    <ServiceCard 
                        title="RDS Instances" 
                        icon={<StorageIcon sx={{ color: theme.palette.primary.main }} />}
                    >
                        {data?.rds?.map((db) => (
                            <Paper 
                                key={db.id} 
                                sx={{ 
                                    p: 2, 
                                    mb: 2,
                                    backgroundColor: theme.palette.background.default
                                }}
                            >
                                <Typography variant="subtitle1" fontWeight="bold">
                                    ID: {db.id}
                                </Typography>
                                <Typography>
                                    Status: <span style={{ 
                                        color: db.status === 'available' ? 
                                            theme.palette.success.main : 
                                            theme.palette.error.main 
                                    }}>
                                        {db.status}
                                    </span>
                                </Typography>
                                <Typography>Engine: {db.engine}</Typography>
                            </Paper>
                        ))}
                    </ServiceCard>
                </Grid>

                {/* Lambda Functions */}
                <Grid item xs={12} md={4}>
                    <ServiceCard 
                        title="Lambda Functions" 
                        icon={<FunctionsIcon sx={{ color: theme.palette.primary.main }} />}
                    >
                        {data?.lambda?.length ? (
                            data.lambda.map((func) => (
                                <Paper 
                                    key={func.name}
                                    sx={{ 
                                        p: 2, 
                                        mb: 2,
                                        backgroundColor: theme.palette.background.default
                                    }}
                                >
                                    <Typography variant="subtitle1" fontWeight="bold">
                                        {func.name}
                                    </Typography>
                                    <Typography>Runtime: {func.runtime}</Typography>
                                    <Typography>Memory: {func.memory}MB</Typography>
                                </Paper>
                            ))
                        ) : (
                            <Typography color="textSecondary">No Lambda functions found</Typography>
                        )}
                    </ServiceCard>
                </Grid>

                {/* ECS Clusters */}
                <Grid item xs={12} md={4}>
                    <ServiceCard 
                        title="ECS Clusters" 
                        icon={<DnsIcon sx={{ color: theme.palette.primary.main }} />}
                    >
                        {data?.ecs?.length ? (
                            data.ecs.map((cluster) => (
                                <Paper 
                                    key={cluster.name}
                                    sx={{ 
                                        p: 2, 
                                        mb: 2,
                                        backgroundColor: theme.palette.background.default
                                    }}
                                >
                                    <Typography variant="subtitle1" fontWeight="bold">
                                        {cluster.name}
                                    </Typography>
                                    <Typography>Status: {cluster.status}</Typography>
                                    <Typography>Tasks: {cluster.tasks}</Typography>
                                </Paper>
                            ))
                        ) : (
                            <Typography color="textSecondary">No ECS clusters found</Typography>
                        )}
                    </ServiceCard>
                </Grid>

                {/* EKS Clusters */}
                <Grid item xs={12} md={4}>
                    <ServiceCard 
                        title="EKS Clusters" 
                        icon={<HubIcon sx={{ color: theme.palette.primary.main }} />}
                    >
                        {data?.eks?.length ? (
                            data.eks.map((cluster) => (
                                <Paper 
                                    key={cluster.name}
                                    sx={{ 
                                        p: 2, 
                                        mb: 2,
                                        backgroundColor: theme.palette.background.default
                                    }}
                                >
                                    <Typography variant="subtitle1" fontWeight="bold">
                                        {cluster.name}
                                    </Typography>
                                    <Typography>Status: {cluster.status}</Typography>
                                    <Typography>Version: {cluster.version}</Typography>
                                </Paper>
                            ))
                        ) : (
                            <Typography color="textSecondary">No EKS clusters found</Typography>
                        )}
                    </ServiceCard>
                </Grid>

                {/* Website Monitoring */}
                <Grid item xs={12}>
                    <WebsiteMonitoring />
                </Grid>

                {/* Log Management */}
                <Grid item xs={12}>
                    <LogManagement />
                </Grid>

                {/* APM Dashboard */}
                <Grid item xs={12}>
                    <APMDashboard metrics={data?.apmMetrics} />
                </Grid>

                {/* RUM Dashboard */}
                <Grid item xs={12}>
                    <RUMDashboard metrics={data?.rumMetrics} />
                </Grid>

                {/* Database Metrics */}
                <Grid item xs={12}>
                    <DatabaseMetrics metrics={data?.dbMetrics} />
                </Grid>
            </Grid>
        </Container>
    );
};

export default Dashboard;
